import styles from "./Css/AddUser.module.css";
import Button from "../UI/Button";
import { useState } from "react";
import Card from "../UI/Card";
import ErrorModel from "../UI/ErrorModel";
import classs from "../UI/Css/ErrorModal.module.css";

function AddUser(params) {
  const [username, setUserName] = useState("");
  const [age, setAge] = useState("");
  const [error, setError] = useState({});

  function inputHandler(identifier, value) {
    if (identifier === "age") {
      if (value <= 0) {
        setError({
          title: "Age can't be (<0)",
          message: "verify age input!",
        });
      } else if (value.length === 0) {
        setError({
          title: "age field can't be empty",
          message: "write your age",
        });
      } else setAge(value);
    } else {
      if (value.length === 0) {
        setError({
          title: "name field can't be empty",
          message: "write your name",
        });
      } else setUserName(value);
    }
  }

  function addUserHandler(event) {
    event.preventDefault();
    const userData = {
      name: username,
      age: age,
    };

    params.dataSender(userData.name, userData.age);

    setAge("");
    setUserName("");
  }

  return (
    <div>
      <div className={classs.backdrop} />
      <ErrorModel title={error.title} message={error.message} />
      <Card className={styles.input}>
        <form onSubmit={addUserHandler}>
          <label htmlFor='username'>Username</label>
          <input
            id='username'
            type='text'
            value={username}
            onChange={(event) => inputHandler("username", event.target.value)}
          />
          <label htmlFor='age'>User Age</label>
          <input
            id='age'
            type='number'
            value={age}
            onChange={(event) => inputHandler("age", event.target.value)}
          />
          <Button type='submit' value='Add user' />
        </form>
      </Card>
    </div>
  );
}
export default AddUser;
