import styles from "./Css/AddUser.module.css";
import Button from "../UI/Button";

import { useState } from "react";
function AddUser(params) {
  const [userName, setUserName] = useState[""];
  const [age, setAge] = useState[""];

  function inputHandler(identifier, value) {
    if (identifier === "username") {
      setUserName(value);
    } else {
      setAge(value);
    }
  }

  return (
    <div className={styles.input}>
      <label className={styles.label}>User Name</label>
      <input
        name='userNameInput'
        type='text'
        className={styles.inputField}
        value={userName}
        onChange={(event) => inputHandler("username", event.target.value)}
      />
      <label className={styles.label}>User Age</label>
      <input
        name='ageInput'
        type='number'
        className={styles.inputField}
        value={age}
        onChange={(event) => inputHandler("age", event.target.value)}
      />
      <Button value='Add user' />
    </div>
  );
}
export default AddUser;
