import styles from "./Css/AddUser.module.css";
import Button from "../UI/Button";
import { useState } from "react";
import Card from "../UI/Card";

function AddUser(params) {
  const [username, setUserName] = useState("");
  const [age, setAge] = useState("");

  function inputHandler(identifier, value) {
    if (identifier === "age") {
      setAge(value);
    } else {
      setUserName(value);
    }
  }

  function addUserHandler(event) {
    event.preventDefault();
    const userData = {
      name: username,
      age: age,
    };

    params.dataSender(userData);
  }

  return (
    <Card className={styles.input}>
      <form action={addUserHandler}>
        <label htmlFor='username'>Username</label>
        <input
          id='username'
          type='text'
          value={username}
          onChange={(event) => inputHandler("username", event.target.value)}
        />
        <label htmlFor='age'>User Age</label>
        <input
          id='age'
          type='number'
          value={age}
          onChange={(event) => inputHandler("age", event.target.value)}
        />
        <Button type='submit' value='Add user' />
      </form>
    </Card>
  );
}
export default AddUser;
