import styles from "./Css/AddUser.module.css";
import Button from "../UI/Button";
import { useState } from "react";

function AddUser(params) {
  const [username, setUserName] = useState("");
  const [age, setAge] = useState("");

  function inputHandler(identifier, value) {
    if (identifier === "age") {
      setAge(value);
    } else {
      setUserName(value);
    }
  }

  const userData = {
    name: username,
    age: age,
  };

  params.dataSender(userData);

  function addUserHandler(event) {
    event.preventDefault();
  }

  return (
    <form action={addUserHandler} className={styles.input}>
      <label htmlFor='username' className={styles.label}>
        Username
      </label>
      <input
        id='username'
        type='text'
        className={styles.inputField}
        value={username}
        onChange={(event) => inputHandler("username", event.target.value)}
      />
      <label htmlFor='age' className={styles.label}>
        User Age
      </label>
      <input
        id='age'
        type='number'
        className={styles.inputField}
        value={age}
        onChange={(event) => inputHandler("age", event.target.value)}
      />
      <Button value='Add user' />
    </form>
  );
}
export default AddUser;
