import styles from "./Css/AddUser.module.css";
import Button from "../UI/Button";
import { useState } from "react";
import Card from "../UI/Card";
import ErrorModel from "../UI/ErrorModel";
import classs from "../UI/Css/ErrorModal.module.css";

function AddUser(params) {
  const [username, setUserName] = useState("");
  const [age, setAge] = useState("");

  function inputHandler(identifier, value) {
    if (identifier === "age") {
      if (value <= 0) {
        console.log("error");
      } else setAge(value);
    } else {
      setUserName(value);
    }
  }

  function addUserHandler(event) {
    event.preventDefault();
    const userData = {
      name: username,
      age: age,
    };

    params.dataSender(userData.name, userData.age);

    setAge("");
    setUserName("");
  }

  return (
    <div>
      <div>
        <ErrorModel title='An error occured!' message='Something went wrong!' />
        <Card className={styles.input}>
          <form onSubmit={addUserHandler}>
            <label htmlFor='username'>Username</label>
            <input
              id='username'
              type='text'
              value={username}
              onChange={(event) => inputHandler("username", event.target.value)}
            />
            <label htmlFor='age'>User Age</label>
            <input
              id='age'
              type='number'
              value={age}
              onChange={(event) => inputHandler("age", event.target.value)}
            />
            <Button type='submit' value='Add user' />
          </form>
        </Card>
      </div>
      <div />
    </div>
  );
}
export default AddUser;
